INSERT INTO `pre_xngs_jkt_question` VALUES (1886,1,1086,'客车遇险后，没有安全门的，可用车上配备的铁锤或者其他物品将车窗玻璃敲破，从窗口疏散乘客。','null','5',2,'5705',0,0);
