INSERT INTO `pre_xngs_jkt_question` VALUES (1888,1,1088,'驾驶人逃离火灾如果无法开启驾驶室门或车门时，应敲碎前挡风玻璃脱离。','null','5',2,'5710',0,0);
INSERT INTO `pre_xngs_jkt_question` VALUES (1889,1,1089,'安装乘客安全带的车辆，应要求乘客在起步前按规定使用安全带。','null','5',2,'5711',0,0);
INSERT INTO `pre_xngs_jkt_question` VALUES (1890,1,1090,'持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人联系电话、联系地址以及从业单位等信息发生变化的，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。','null','5',2,'5713',0,0);
