  `id` bigint(20) NOT NULL auto_increment,
  `userid` int(11) NOT NULL default '0',
  `onelevel` tinyint(3) NOT NULL default '0' ,
  `onelevelname` varchar(40) NOT NULL default '',
  `jzmc` varchar(40) NOT NULL default '' ,
  `sname` varchar(10) default NULL ,
  `school` varchar(40) default NULL ,
  `starttime` varchar(22) NOT NULL default '0',
  `submittime` varchar(22) default '0',
  `score` tinyint(3) default '0',
